import React, { useState } from "react";
import {Link} from 'react-router-dom';
import styles from './Home.module.css'
import InputControl from "../inputControl/Inputcontrol";
import { getAuth, signOut } from "firebase/auth";

function Home(){
    const [errMsg, setErrMsg] = useState("");
    const [values, setValues] = useState({
        Title:"",
        Task:"",
    })
    // const [errMsg, setErrMsg] = useState("");
    const auth = getAuth();
signOut(auth).then(() => {
    console.log("Log Out")
}).catch((error) => {
  console.log("error");
});

// handle add task btn
const handleAddTask =()=>{
    if(!values.Title){
        setErrMsg("Please Add Task Field");
        return;
    } if(!values.Task){
        setErrMsg("Please Add Task Fields");
        return;
    }
    setErrMsg("");
    setSubmitButtonDisabled(true);
}


    return(
        <div className={styles.container}>
        <div className={styles.heading}>
        <h1>TODO Home Application</h1>
        <Link to="/Login"><button className={styles.logOutbtn}>Log Out</button></Link>
        </div>
        <div className={styles.inputfield}>
          <div className={styles.innerBox}>
          <InputControl
          label="Title" 
          placeholder="Task Title"
          onChange={(event)=>
           setValues((prev) =>({...prev, Title:event.target.value}))}/>
           <InputControl
          label="Task" 
          placeholder="Enter Task"
          onChange={(event)=>
           setValues((prev) =>({...prev, Task:event.target.value}))}/>
          <button className={styles.addbtn}>Add</button>
          </div>

          <div className={styles.taskEditContainer}>

          <button className={styles.updatebtn}>Update Task</button>
          </div>

          </div>
          {/* task added field */}
          <div className={styles.taskAddedContainer}>

          </div>
         </div>
    )
}

export default Home;
